package week1.day1;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=-40;
		if(num<0)
		{
			int res=(num*-1);
			
			System.out.print("The number is " + num);
			System.out.println(" and converted to " +res);
			
		
		}
	}

}
